import React, {useState} from "react"
import { json } from "react-router-dom"
import XLSX from "xlsx"
import Hierarchy from "./Dropdown_Gen"

export default function About() {
    const[jsonData, setData] = useState([]);
    const handleFile = async(e) => {
        const file = e.target.files[0];
        const data = await file.arrayBuffer();
        const workbook = XLSX.readFile(data, {sheetRows: 6});
        const worksheet = workbook.Sheets[workbook.SheetNames[3]];
        const jsonData = XLSX.utils.sheet_to_json(worksheet, {header: 1, blankrows:false})
        setData(jsonData)
    }
    
    const Hierarchies= [];
    const HierarchyMap = new Map();

    for(let data in jsonData){
        if(HierarchyMap.has(data[0])){
            HierarchyMap.get(data[0]).addItem(data);
        }
        else{
            HierarchyMap.set(data[0], new Hierarchy(data, false));
            Hierarchies.push(HierarchyMap.get(data[0]));
        }
    }
    console.log(jsonData) 
    return (
        <div>
            <input type="file" onChange={(e) => handleFile(e)}/>
            {Hierarchies.map((item, i) => 
            <div key={i}> {item.returnHTML}</div>)}
        </div>
    )
}
